        this.audioContext = null;
        this.scriptProcessor = null;
        this.isInitialized = false;
        this.isPaused = false;
        this.currentResponse = [];
        this.runningStep = 0;
        
        this.currentLanguage = localStorage.getItem('preferredLanguage') || 'en';

